import React, { Component } from 'react'
import Table from 'react-bootstrap/Table'
import Container from 'react-bootstrap/Container'
import Form from 'react-bootstrap/Form'

export default class Cities extends Component {
    constructor(props) {
        super(props);
        this.filterCities.
        this.state = {
            isLoading: false,
            citiesArray: [],
            filteredCitiesArray: []
        };
    }

    fetchCities = () => {

        fetch('http://localhost:5000/cities/all')
            .then(response => response.json())
            .then(citiesArray => this.setState({ citiesArray, isLoading: true }))
            .catch(error => console.log(error))
    }

    filterCities = (filtro) => {
        console.log(filtro)
    }

    componentDidMount() {
        this.fetchCities()
    }

    render() {

        if (this.state.isLoading === false) {
            return (
                <Container>
                    <h6 className="d-flex justify-content-center">Waiting for cities list to load</h6>
                </Container>
            )
        }

        else return (

            <Container>
                <h2 className="d-flex justify-content-center mb-3">Cities</h2>

                <Form className="mb-4">
                    <Form.Label>Filter Cities</Form.Label>
                    <Form.Control type="city" placeholder="Enter city" onChange={this.filterCities}/>
                </Form>

                <Table>

                    <thead>
                        <tr>
                            <th>Country</th>
                            <th>City</th>
                        </tr>
                    </thead>

                    <tbody>
                        {this.state.citiesArray.map((city) =>
                            <tr key={city._id}>
                                <td> {city.country} </td>
                                <td> {city.city} </td>
                            </tr>
                        )}
                    </tbody>

                </Table>

            </Container>
        )
    }
}
