import { FETCH_ALL_ITINERARIES } from '../actions/itinerariesActions'

const initialState = {
    itinerariesArray: [],
    loadingItinerary: false,
    errorFetch: ''
}

export function itinerariesReducer(state = initialState, action){
    switch(action.type){
        case FETCH_ALL_ITINERARIES:
            return Object.assign({}, state, { itinerariesArray: action.itineraries , loadingItinerary: false})
        default:
            return state;
    }
}

export default itinerariesReducer;
