import React, { Component } from 'react'

export default class Cities extends Component {
    constructor (){
        super();
        this.state = {
            citiesArray: []
        };
    }
    
    async fetchCities() {

        const cities = await fetch('http://localhost:5000/cities/all');
        let citiesArray = await cities.json();
        this.setState()




/*         fetch('http://localhost:5000/cities/all')
            .then(response => response.json())
            .then(citiesArray => {this.setState({citiesArray});
            console.log(this.state.citiesArray)})
               .then(result => this.setState({citiesArray}))
            .catch(error => console.log(error)) */
    }

    componentDidMount() {
        this.fetchCities()
    }

    render() {
        return (
            <div>
                Cities
                <pre>
                    {this.state.citiesArray.map((city) =>
                        <li> {city} </li>
                        )}
                </pre>
            </div>  
        )
    }
}
