import React, { Component } from 'react'
import Table from 'react-bootstrap/Table'
import Container from 'react-bootstrap/Container'
import Form from 'react-bootstrap/Form'
import NavBar from './NavBar'
import Image from 'react-bootstrap/Image'

export default class Cities extends Component {

    constructor() {
        super();
        this.state = {
            isLoading: false,
            citiesArray: [],
            filteredCitiesArray: []
        };
    }

    fetchCities = () => {

        fetch('http://localhost:5000/cities/all')
            .then(response => response.json())
            .then(citiesArray => this.setState({ citiesArray, isLoading: true, filteredCitiesArray: citiesArray }))
            .catch(error => console.log(error))

    }

    filterCities = (filter) => {
        var filteredCitiesArray = [];
        var value = filter.target.value.toLowerCase()

        this.state.citiesArray.map(element => {
            if (element.city.toString().toLowerCase().startsWith(value))
                filteredCitiesArray.push(element)
        })

        this.setState({ filteredCitiesArray })
    }

    componentDidMount() {
        this.fetchCities()
    }

    render() {

        if (this.state.isLoading === false) {
            return (
                <Container>

                    <NavBar>

                    </NavBar>

                    <h6 className="d-flex justify-content-center mt-3">Waiting for cities list to load</h6>
                </Container>
            )
        }

        else return (

            <Container>

                <NavBar>

                </NavBar>
                <h2 className="d-flex justify-content-center mt-3 mb-3">Cities</h2>

                <Form className="mb-4">
                    <Form.Label>Filter Cities</Form.Label>
                    <Form.Control type="city" placeholder="Enter city" onChange={this.filterCities} />
                </Form>

                <Table>

                    <thead>
                        <tr>
                            <th>Country</th>
                            <th>City</th>
                        </tr>
                    </thead>

                    <tbody>
                        <tr>
                            <td>
                                <Image
                                    className="d-block w-100 h-100 mb-3 justify-center"
                                    src="http://www.photoguide.to/wp-content/uploads/2015/10/barcelona.jpg"
                                />

                            </td>
                        </tr>
                        {this.state.filteredCitiesArray.map((city) =>
                            <tr key={city._id}>
                                <td> {city.country} </td>
                                <td> {city.city} </td>
                            </tr>
                        )}
                    </tbody>

                </Table>

            </Container>
        )
    }
}
