import React, { Component } from 'react'

export default class Cities extends Component {
    constructor (){
        super();
        this.state = {
            citiesArray: []
        };
    }
    
    fetchCities = () => {
        fetch('http://localhost:5000/cities/all')
            .then(response => response.json())
            .then(result => this.state.citiesArray.push(result))
            //            .then(result => this.setState({citiesArray: result}))
            .catch(error => console.log(error))
    }

    componentDidMount() {
        this.fetchCities()
    }

    render() {
        return (
            <div>
                Cities
                <pre>
                    {this.state.citiesArray.map((city) =>
                        <li> {city} </li>
                        )}
                </pre>
            </div>  
        )
    }
}
