import React, { Component } from 'react'
import './Cities.css'
import Image from 'react-bootstrap/Image'
import Container from 'react-bootstrap/Container'

export default class Itinerary extends Component {
    state = {
        city: {},
        isLoading: true
    }

    componentDidMount() {
        console.log(this.props.match.params.name)
        const name = this.props.match.params.name;
        const url = "http://172.16.127.35:5000/cities/" + name;
        console.log(url);
        fetch(url)
            .then(response => response.json())
            .then(data => this.setState({ city: data, isLoading: false }))
            .catch(err => console.log(err));
    }

    render() {
        if (this.state.isLoading === true) {
            return (
                <Container>
                    <h6 className="d-flex justify-content-center mt-3">
                        <i>Waiting still we load the cities info...</i></h6>
                </Container>
            )
        }

        return (
            <div>
                <h2>{this.state.city.city}</h2>

            </div>
        )
    }
}
